SELECT * FROM oc_ecs_server
instanceChargeType = "PrePaid" AND expiredTime <= DATE_FORMAT(#{expiredTime},'%Y-%m-%d %H:%i:%s')
AND serverName like "%" #{serverName} "%"
AND publicIp LIKE "%"#{queryIp}"%" OR insideIp LIKE "%"#{queryIp}"%"
AND status = #{status}
ORDER BY expiredTime ,serverName DESC limit #{pageStart}, #{length}

