mapper.select:
SELECT COUNT(*) AS cnt,  SUM(memory) AS memoryCnt ,  SUM(cpu) AS cpuCnt
FROM physical_server
WHERE useType = 1;

SELECT COUNT(*) AS cnt,  SUM(memory) AS memoryCnt ,  SUM(cpu) AS cpuCnt
FROM physical_server
WHERE useType = 2;

