creationTime >= #{startDate} AND creationTime < #{endDate}
ORDER BY creationTime DESC

SELECT * FROM new_vm_server WHERE
gmtCreate >= #{startDate} AND gmtCreate < #{endDate}
ORDER BY gmtCreate DESC

