mapper.update:
UPDATE oc_server_group
SET name = #{name},
  content = #{content},
  ipNetwork = #{ipNetwork},
  useType = #{useType}
WHERE id = #{id}

mapper.insert:
INSERT ignore INTO new_sg_in_relation(
  serverGroupId,
  ipNetworkId,
  ipType
) VALUES (
  #{serverGroupId},
  #{ipNetworkId},
  #{ipType}
);

mapper.delete:
DELETE FROM new_sg_in_relation
WHERE serverGroupId = #{serverGroupId}
AND ipNetworkId = #{ipNetworkId}

mapper.select:
SELECT a1.* FROM keybox_user_server a0 JOIN oc_server_group a1 ON a0.serverGroupId = a1.id
WHERE a0.username = #{username}
GROUP BY a1.id

SELECT * FROM oc_server_group


        id IN ( select serverGroupId from keybox_user_server where username = #{username} )


        AND useType = #{useType}


        AND name LIKE "%" #{name} "%"


ORDER BY name DESC
limit #{start}, #{length}

SELECT * FROM oc_server_group AS sg LEFT JOIN project_property AS property ON sg.id = property.propertyValue AND property.propertyType = 1
name LIKE "%" #{name} "%"
AND useType = #{useType}
AND propertyValue IS NULL
ORDER BY name DESC limit #{start}, #{length}

SELECT * FROM oc_server_group_usetype
typeName LIKE "%" #{typeName} "%"
ORDER BY useType limit #{start}, #{length}

mapper.update:
UPDATE oc_server_group_usetype
SET useType = #{useType},
typeName = #{typeName},
color = #{color},
content = #{content}
WHERE id = #{id}
